package String;

import java.util.Scanner;

public class concatinate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		String s1=s.next();
		String s2= s.next();
		String res=s1+s2;
		System.out.println(res.toLowerCase());

	}

}
